import React, { useState, useContext } from 'react'
import { Link, useNavigate } from "react-router-dom"
import { nanoid } from 'nanoid';
import { useLocation } from 'react-router-dom'
import { AppContext } from '../../App';
import Note from './Note'
import AddNote from './AddNote'

const NotesList = () => {
    const { data, notes, setNotes, setDetailData } = useContext(AppContext);
    let location = useLocation();
    let navigate = useNavigate();
    const jobid = location.state.jobid;

    const handleDeleteNote = (id) => {
        const newNotes = notes.filter((note) => note.id !== id);
        setNotes(newNotes);
    };

    const handleAddNote = (text) => {
        const date = new Date();
        const newNote = {
            jobid: jobid,
            id: nanoid(),
            text: text,
            date: date.toLocaleDateString(),
        };
        console.log(notes);
        setNotes([...notes, newNote]);
    };


    const transferToDetail = (d) => {
        setDetailData(data.filter((d) => d.id === jobid));
        navigate('/Detail');
    }

    return (
        <div className='note-list'>
            {notes.length > 0 && notes.filter((n) => n.jobid === jobid).map((note) => (
                <Note key={note.id}
                    id={note.id}
                    text={note.text}
                    date={note.date}
                    handleDeleteNote={handleDeleteNote}
                />
            ))}
            <AddNote handleAddNote={handleAddNote} />

            {/* <Link to='/'>Go Back</Link> */}
            <button type="button" className="btn btn-link" onClick={() => transferToDetail()}>Save</button>
        </div>
    )
}

export default NotesList;
